from django.db import migrations


def copy_src_to_dst(Source, Destination):
    '''
    Para cada ABC, Instanciar um MTI com todos os atributos
    salvar o MTI
    associar os speakers do ABC no MTI
    deletar o ABC
    '''

    # logica
    for src in Source.objects.all():
        dst = Destination(
            title=src.title,
            start=src.start,
            description=src.description,
            slots=src.slots)

        # salvando
        dst.save()

        # associano o speakers ao novo Course. definino q os speakers do MTI Ã© o mesmo do ABC
        dst.speakers.set(src.speakers.all())

        # Deletando o ABC
        src.delete()


